import React, {/*Component,*/useState} from 'react';
import {Link} from 'react-router-dom';
import DatePicker from 'react-datepicker';
import logo from "./doc.jpeg"
import "react-datepicker/dist/react-datepicker.css";
import {connect} from 'react-redux';
import {book} from '../actions/index';
//import Calendar from 'react-calendar';


function Schedule(props) {

    //const[hours,setHours]=useState('');

    const[alertError,setAlertError]=useState(false);
    const[name,setName]=useState('');
    const[email,setEmail]=useState('');
    const[phno,setPhno]=useState('');
    const [startDate, setStartDate]=useState(new Date());
    //console.log(selectedDate);

    return (
    <div className="row schedule">
        <div className="col-sm-12 col-md-5 col-lg-3 info">
            <Link to='/'>
                <button className="btn btn-danger buttonBack">Back</button>
            </Link><br/><br/>
            <img src={logo} className="logo" alt="doc"/><br/>
            <p>
                <i className='fa fa-user-md'></i> Dr. ABC
            </p><hr/>
            <p>
                <i className='fa fa-history'></i> Book an hourly slot
            </p><hr/>
            <p>Working hrs. on Weekdays</p>
            <ul> 
                <li>9AM - 5PM</li>
                <li>1PM - 2PM(Lunch Break)</li>
            </ul><hr/>
            <p>Working hrs. on Saturday</p>
            <ul> 
                <li>9AM - 2PM</li>
            </ul><hr/>
            <p>
                <i className='fa fa-times'></i> Closed on Sundays
            </p><hr/>
        </div>


        <div className="col-sm-12 col-md-7 col-lg-9 details">

                <div>
                {
                    alertError? <div><h1>Hide and Show</h1></div> : null
                }
            </div>
            <div className="input-group">
                <div className="input-group-prepend">
                    <span className="input-group-text" id="inputGroup-sizing-default"><i className='fa fa-user'></i></span>
                </div>
                <input id="name" placeholder="Name" required type="text" value={name} onChange={(e)=>{setName(e.target.value)}}/>
            </div>


            <div className="input-group">
                <div className="input-group-prepend">
                    <span className="input-group-text" id="inputGroup-sizing-default"><i className='fa fa-envelope'></i></span>
                </div>
                <input id="email" placeholder="Email" required type="text" value={email} onChange={(e)=>{setEmail(e.target.value)}}/>
            </div>

            <div className="input-group">
                <div className="input-group-prepend">
                    <span className="input-group-text" id="inputGroup-sizing-default"><i className='fa fa-phone'></i></span>
                </div>
                <input id="phno" placeholder="Contact No." type="number" value={phno} onChange={(e)=>{setPhno(e.target.value)}}/>
            </div>

            <div className="input-group">
                <div className="input-group-prepend">
                    <span className="input-group-text" id="inputGroup-sizing-default"><i className='fa fa-calendar'></i></span>
                </div>
                <DatePicker className="datepicker"
                selected={startDate} 
                onChange={date => setStartDate(date)}
                minDate={new Date()}
                //isClearable
                showTimeSelect
                timeIntervals={60}
                timeCaption="Time"
                dateFormat="dd/MM/yyyy"
                filterDate={date => date.getDay()!==0}
                />
            </div>

            <button onClick={()=>{ if(name.length===0 || phno.length===0 || email.length===0 ){setAlertError(true)}
                                else if(phno.length<10 || phno.length>10){alert("Enter 10 digits")}
                                else if(!email.includes('@') && !email.includes('.com')){alert('Invalid email')}
                                else {setAlertError(false)
                                    props.submit(name, email, phno, startDate)} }} 
                                className="btn btn-success">Book</button>
        </div>
    </div>
    );
}



const mapDispatchToProps = dispatch => {
    return {
        submit:(name,email,phno,startDate) => {
            dispatch(book(/*name,email,phno,startDate*/)/*{type:book().type,payload:{name:name, email:email, phno:phno, startDate:startDate}}*/)
        }
    }
}

export default connect(null,mapDispatchToProps)(Schedule);

