import React, {Component} from 'react';
import {Link} from 'react-router-dom';
import DatePicker from 'react-datepicker';
import "react-datepicker/dist/react-datepicker.css";


/*function Schedule() {

    const [selectedDate, setSelectedDate]=useState(null);
    //console.log(selectedDate);
    return (
    <div>
        <Link to='/'>
            <button className="btn btn-danger buttonBack">Back</button>
        </Link>
        <DatePicker 
            selected={selectedDate} 
            onChange={date => setSelectedDate(date)} 
            minDate={new Date()}
            isClearable
            showTimeSelect
              timeFormat="HH:mm"
              timeIntervals={60}
              timeCaption="time"
            />

        <button>Show</button>
    </div>
    );

}*/

class Schedule extends Component {

    constructor (props) {
      super(props)
      this.state = {
        name: "",
        email:"",
        phno:"",
        startDate:new Date()
      };
      this.handleChangeDate = this.handleChangeDate.bind(this);
      this.handleChangeName = this.handleChangeName.bind(this);
      this.handleChangeEmail = this.handleChangeEmail.bind(this);
      this.handleChangePhno = this.handleChangePhno.bind(this);
      this.onFormSubmit = this.onFormSubmit.bind(this);
    }

    handleChangeName(event) {
        this.setState({name: event.target.value});
    }

    handleChangeEmail(event) {
        this.setState({email: event.target.value});
    }

    handleChangePhno(event) {
        this.setState({phno: event.target.value});
    }

    handleChangeDate(date) {
      this.setState({
        startDate: date
      })
    }

    onFormSubmit(e) {
      e.preventDefault();
      //if(this.state.startDate.getHours())
      console.log(this.state.startDate.getHours())
    }

    render() {
      return (
        <div className="schedule">
            <Link to='/'>
                <button className="btn btn-danger buttonBack">Back</button><br/>
            </Link>
            <form onSubmit={ this.onFormSubmit }>
                <div className="form-group">

                    <div className="input-group">
                        <div className="input-group-prepend">
                            <span className="input-group-text" id="inputGroup-sizing-default"><i className='fa fa-user'></i></span>
                        </div>
                        <input id="name" type="text" value={this.state.name} onChange={this.handleChangeName}/>
                    </div>

                    <div className="input-group">
                        <div className="input-group-prepend">
                            <span className="input-group-text" id="inputGroup-sizing-default"><i className='fa fa-envelope'></i></span>
                        </div>
                        <input id="email" type="text" value={this.state.email} onChange={this.handleChangeEmail}/>
                    </div>

                    <div className="input-group">
                        <div className="input-group-prepend">
                            <span className="input-group-text" id="inputGroup-sizing-default"><i className='fa fa-phone'></i></span>
                        </div>
                        <input id="phno" type="number" value={this.state.phno} onChange={this.handleChangePhno}/>
                    </div>


                    <div className="input-group">
                        <div className="input-group-prepend">
                            <span className="input-group-text" id="inputGroup-sizing-default"><i className='fa fa-calendar'></i></span>
                        </div>
                        <DatePicker className="datepicker"
                        selected={ this.state.startDate }
                        onChange={ this.handleChangeDate }
                        minDate={new Date()}
                        //isClearable
                        showTimeSelect
                        timeIntervals={30}
                        timeCaption="Time"
                        dateFormat="dd/MM/yyyy h:mm aa"
                        filterDate={date => date.getDay()!==0}
                        />
                        <button className="btn btn-primary">1 hr</button>
                        <button className="btn btn-warning">30 min</button>
                    </div>

                    <button className="btn btn-success">Book</button>
                </div>
            </form>
        </div>
      );
    }

  }


export default Schedule;
