class Schedule extends Component {

    constructor (props) {
      super(props)
      this.state = {
        name: "",
        email:"",
        startDate: ""
      };
      this.handleChangeDate = this.handleChangeDate.bind(this);
      this.handleChangeName = this.handleChangeName.bind(this);
      this.handleChangeEmail = this.handleChangeEmail.bind(this);
      this.onFormSubmit = this.onFormSubmit.bind(this);
    }

    handleChangeName(event) {
        this.setState({name: event.target.value});
    }

    handleChangeEmail(event) {
        this.setState({email: event.target.value});
    }

    handleChangeDate(date) {
      this.setState({
        startDate: date
      })
    }

    onFormSubmit(e) {
      e.preventDefault();
      console.log(this.state)
    }

    render() {
      return (
        <div>
            <Link to='/'>
                <button className="btn btn-danger buttonBack">Back</button><br/>
            </Link>
            <form onSubmit={ this.onFormSubmit }>
                <div className="form-group">

                    <div className="input-group">
                        <div className="input-group-prepend">
                            <span className="input-group-text" id="inputGroup-sizing-default">Name</span>
                        </div>
                        <input id="name" type="text" value={this.state.name} onChange={this.handleChangeName}/>
                    </div>

                    <div className="input-group">
                        <div className="input-group-prepend">
                            <span className="input-group-text" id="inputGroup-sizing-default">Email</span>
                        </div>
                        <input id="name" type="text" value={this.state.email} onChange={this.handleChangeEmail}/>
                    </div>


                    <div className="input-group">
                        <div className="input-group-prepend">
                            <span className="input-group-text" id="inputGroup-sizing-default">Date</span>
                        </div>
                        <DatePicker className="datepicker"
                        selected={ this.state.startDate }
                        onChange={ this.handleChangeDate }
                        minDate={new Date()}
                        //isClearable
                        showTimeSelect
                        timeFormat="HH:mm"
                        timeIntervals={60}
                        timeCaption="time"
                        dateFormat="dd/MM/yyyy"
                        />
                        <button className="btn btn-primary">1hour</button>
                    </div>

                    <button className="btn btn-primary">Show Date</button>
                </div>
            </form>
        </div>
      );
    }

  }


export default Schedule;
