const iState = {name:"",email:"",phno:"",startDate:new Date()}

const reducer = (state=iState, action) => {
    if(action.payload.name==='') alert('kdcdk')
    else if(action.type==='SUBMIT'){
        return action.payload
    }
    return state
}

export default reducer
