//const iState = {name:"",email:"",phno:"",startDate:new Date()}
const iState = {users:[]}

const reducer = (state=iState, action) => {
    switch(action.type){
        case 'BOOK':
            return{
                ...state,
                users:[...state.users,payload]
            }
            //return action.payload
        default:
            return state
    }
}

export default reducer
