function reducer(state = iState, action) {
    switch (action.type) {
      case 'ADD_BOOK_STARTED':
        return {
          ...state,
          loading: true
        };
      case 'ADD_BOOK_SUCCESS':
        return {
          ...state,
          loading: false,
          error: null,
          users: [...state.users, action.payload]
        };
      case 'ADD_BOOK_FAILURE':
        return {
          ...state,
          loading: false,
          error: action.payload.error
        };
      default:
        return state;
    }
  }

export default reducer
