import React, {/*Component,*/useState} from 'react';
import {Link} from 'react-router-dom';
import DatePicker from 'react-datepicker';
import logo from "./doc.jpeg"
import "react-datepicker/dist/react-datepicker.css";
import {connect} from 'react-redux';
import {book} from '../actions/index'; 
//import store from '../index'
//import Calendar from 'react-calendar';


function Schedule(props) {

    const[detailError,setDetailError]=useState(false);
    const[phnoError,setPhnoError]=useState(false);
    const[emailError,setEmailError]=useState(false);
    const[name,setName]=useState('');
    const[email,setEmail]=useState('');
    const[phno,setPhno]=useState('');
    const [startDate, setStartDate]=useState(new Date());

    return (
    <div className="row schedule">
        <div className="col-sm-12 col-md-5 col-lg-5 info">
            <Link to='/'>
                <button className="btn btn-outline-secondary buttonBack">Back</button>
            </Link>

            <br/><br/>
            <img src={logo} className="logo" alt="doc"/><br/>

                <h1><i className='fa fa-user-md'></i> Dr. ABC</h1>
            <hr/>
            <p>
                <i className='fa fa-list'></i> Fill in details to book meeting
            </p><hr/>
            <p>
                <i className='fa fa-history'></i> Book an hourly slot
            </p><hr/>
            <p><i className='fa fa-briefcase'></i>Working hrs. on Weekdays</p>
            <ul> 
                <li>9AM - 5PM</li>
                <li>1PM - 2PM(Lunch Break)</li>
            </ul><hr/>
            <p><i className='fa fa-briefcase'></i>Working hrs. on Saturday</p>
            <ul> 
                <li>9AM - 2PM</li>
            </ul><hr/>
            <p>
                <i className='fa fa-times'></i> Closed on Sundays
            </p><hr/>
        </div>


        <div className="col-sm-12 col-md-7 col-lg-7 details">
            <div>
                {
                    props.booked==='book success'? <div className="alert alert-success" role="alert">
                                                        Booked
                                                </div> : null
                }
            </div>

            <div>
                {
                    props.booked==='book fail'? <div className="alert alert-danger" role="alert">
                                                        This slot could not be booked
                                                </div> : null
                }
            </div>

            <div>
                {
                    detailError?<div class="alert alert-warning" role="alert">
                                    Plese Enter All details
                                </div> : null
                }
                </div>

            <div>
            {
                phnoError? <div class="alert alert-warning" role="alert">
                                Plese Enter 10 digits in Contact no.
                            </div> : null
            }
            </div>
            <div>
            {
                emailError? <div class="alert alert-warning" role="alert">
                                Invalid email
                            </div> : null
            }
            </div>
            <div className="input-group">
                <div className="input-group-prepend">
                    <span className="input-group-text" id="inputGroup-sizing-default"><i className='fa fa-user'></i></span>
                </div>
                <input id="name" placeholder="Name" required type="text" value={name} onChange={(e)=>{setName(e.target.value)}}/>
            </div>


            <div className="input-group">
                <div className="input-group-prepend">
                    <span className="input-group-text" id="inputGroup-sizing-default"><i className='fa fa-envelope'></i></span>
                </div>
                <input id="email" placeholder="Email" required type="text" value={email} onChange={(e)=>{setEmail(e.target.value)}}/>
            </div>

            <div className="input-group">
                <div className="input-group-prepend">
                    <span className="input-group-text" id="inputGroup-sizing-default"><i className='fa fa-phone'></i></span>
                </div>
                <input id="phno" placeholder="Contact No." type="number" value={phno} onChange={(e)=>{setPhno(e.target.value)}}/>
            </div>

            <div className="input-group">
                <div className="input-group-prepend">
                    <span className="input-group-text" id="inputGroup-sizing-default"><i className='fa fa-calendar'></i></span>
                </div>
                <DatePicker className="datepicker"
                selected={startDate} 
                onChange={date => setStartDate(date)}
                minDate={new Date()}
                minTime={new Date().setHours(9)}
                maxTime={startDate.getDay()===6 ? new Date().setHours(14):new Date().setHours(18)}
                showYearDropdown
                showMonthDropdown
                //isClearable
                showTimeSelect
                timeIntervals={60}
                timeCaption="Time"
                dateFormat="dd/MM/yyyy"
                filterDate={date => date.getDay()!==0}
                />
            </div>



            <button onClick={()=>{ 
                                if(name.length===0 || phno.length===0 || email.length===0 ){setDetailError(true)}
                                else if(phno.length<10 || phno.length>10){setPhnoError(true);setDetailError(false);setEmailError(false)}
                                else if(!email.includes('@') && !email.includes('.com')){setEmailError(true);setDetailError(false);setPhnoError(false);}
                                else {  setDetailError(false)
                                        setPhnoError(false)
                                        setEmailError(false)
                                    props.submit(name, email, phno, startDate)} }} 
                                className="btn btn-success">Book</button>
        </div>
    </div>
    );
}



